#!/usr/bin/env bash
# Bash completion for pks CLI command
# Generated by cli_taxo

_pks_complete () {

    local cur="${COMP_WORDS[COMP_CWORD]}"
    local prev="${COMP_WORDS[COMP_CWORD-1]}"

    echo "${COMP_WORDS[@]}" | grep -q '-' && {
        i=0
        until echo "${COMP_WORDS[i]}" | grep -q '-' ; do ((i++)); done
        prev="${COMP_WORDS[i-1]}"
    }
    
    case "$prev" in
                pks)    cmds="cluster clusters create-cluster delete-cluster get-credentials help login logout plans resize --help --version";;
        help)    cmds="--help";;
        create-cluster)    cmds="--help --json --non-interactive --wait";;
        cluster)    cmds="--help --json";;
        delete-cluster)    cmds="--help --non-interactive --wait";;
        logout)    cmds="--help";;
        get-credentials)    cmds="--help";;
        clusters)    cmds="--help --json";;
        login)    cmds="--help --skip-ssl-validation";;
        resize)    cmds="--help --json --non-interactive --wait";;
        plans)    cmds="--help --json";;
        *)    cmds="pks help create-cluster cluster delete-cluster logout get-credentials clusters login resize plans";;
    esac

    COMPREPLY=( $(compgen -W "$cmds" -- "$cur") )

    return 0
}

complete -F _pks_complete pks

